import React, { Component } from 'react';
import Conversation from '../Conversations/Conversation';
import {Layout, Menu, Icon, Avatar, Button} from 'antd';
import { Input } from 'antd';
import qoobee from "../../images/qoobee.jpg";
import '../../css/Conversations/conversation.css';

const Search = Input.Search;
const {
    Header, Content, Footer, Sider,
} = Layout;


class Conversations extends Component{
    render() {
        return (
            <div >

                <Layout style={{
                     height: '100%',width:'250px', position: 'fixed',
                }}
                >
                    <div  style={{height:'100px',backgroundColor:'white',
                        borderRight: '1px solid #ebebe0 '}} align="middle">
                        <Search
                            placeholder="input search text"
                            onSearch={value => console.log(value)}
                            style={{ width: 200, margin:'10px auto 10px' }}
                        />
                        <div >
                            <Button shape="circle" icon="user-add" style={{ marginRight:'10px' }}></Button>
                            <Button shape="circle" icon="plus-circle" />
                        </div>
                    </div>
                    <div style={{overflow: 'auto'}} id="contacts">
                    <Menu theme="light" mode="inline" defaultSelectedKeys={['1']} >
                        <Menu.Item key="1" style={{height:'70px', }} >
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="2" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="3" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="4" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="5" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="6" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="7" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                        <Menu.Item key="8" style={{height:'70px', }}>
                            <Avatar  size={50} src={qoobee} style={{margin:'10px 5px 10px 5px'}}/>
                            <span className="nav-text">nav 1</span>
                        </Menu.Item>
                    </Menu>
                    </div>
                </Layout>
            </div>
        );
    }
}
export default Conversations;
