import React, { Component } from 'react';
import {Alert, Avatar, Comment, Layout} from 'antd';
import qoobee from "../../images/qoobee.jpg";
const {
    Header, Footer, Sider, Content,
} = Layout;

const headerStyle = {
    height:'100px', backgroundColor:'white',borderBottom: '1px solid #ebebe0 ', padding:'10px'
}

const footerStyle = {
    height:'80px', backgroundColor:'white',borderTop: '1px solid #ebebe0 '
}

class Message extends Component {

    render() {
        return (
            <Layout>
            <Header style={headerStyle}>
                <Comment
                    author={<a>Han Solo</a>}
                    avatar={(
                        <Avatar
                            src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                            alt="Han Solo"
                        />
                    )}
                    content={(
                        <p>We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.</p>
                    )}
                />
            </Header>,

            <Content>
            </Content>,

            <Footer style={footerStyle}>
            </Footer >

            </Layout>
        );
    }
}
export default Message;
