import React, {Component} from 'react';
import Side from './Side/Side.js';
import Messages from "./Messages/Messages";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import {
    Layout, Menu, Breadcrumb, Icon, Button,
} from 'antd';
import {Redirect} from "react-router";


const {
    Header, Content, Footer, Sider,
} = Layout;


function Home() {
    return (
        <div>
            <h2>Home</h2>
        </div>
    );
}

const About=({isLogin})=> {
    const islogin = isLogin;
    return (

        <div>
            {islogin ? '1':<Redirect to={{pathname: '/login', state: {from: this.props.location}}} />}
            <h2>About</h2>
        </div>
    );
}

function Topic({ match }) {
    return (
        <div>
            <h3>Topics</h3>
        </div>
    );
}

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }
    // handleData(data) {
    //     let result = JSON.parse(data);
    //     this.setState({count: this.state.count + result.movement});
    // }
    componentDidMount() {
        var connection = new WebSocket('ws://localhost:8081/echo');
        connection.onopen = function () {
            connection.send('Ping'); //
        };
        connection.onerror = function (error) {
            console.log('WebSocket Error ' + error);
        };
        connection.onmessage = function (e) {
            console.log('Server: ' + e.data);
        };

    }
    render() {
        return (


            <Router>
                <div>
                    <ul>
                        <li>

                            <Link to="/">Home</Link>
                        </li>
                        <li>
                            <Link to="/about">About</Link>
                        </li>
                        <li>
                            <Link to="/topics">Topics</Link>
                        </li>
                    </ul>

                    <hr />

                    <Route exact path="/" component={Home} />
                    <About isLogin={true} path='/Home'/>

                    {/*<Route path="/topics" component={Topics} />*/}
                </div>
            </Router>
        );
    }
}

export default App;
