import React, {Component} from 'react';
import Authentication from '../Authentication';
import { connect } from 'react-redux'
import {
    Form, Icon, Input, Button, Checkbox, Row, Col,
} from 'antd';
import { BrowserRouter as Redirect } from "react-router-dom";
import {Route} from "react-router";

class LoginRegister extends Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                username: '',
                password: ''
            },
            submitted: false
        };
    }
    handleChange=(event)=>{
        const { name, value } = event.target;
        const { user } = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        // console.log(this.state.user.username);
        //return (<Redirect to={{pathname: '/', state: { from: this.props.location }}}/>);
        // return(
        //     <Route render={(props) => (
        //         <Redirect to='/' />
        //     )} />
        // );
        // this.props.history.push('/home');

        // dispatch({type:"LOGIN", username:"nà ní"})
        this.props.Login("Nà Ní");
    }

    render() {
        return (
            <Row style={{marginTop:'200px'}}>
                <Col span={8} offset={8}>
                    <Form onSubmit={this.handleSubmit} className="login-form">
                        <Form.Item>
                            <Input  prefix={<Icon type="user" style={{color: 'rgba(0,0,0,.25)'}} />}
                                   placeholder="Username" name="username" onChange={this.handleChange}/>
                        </Form.Item>
                        <Form.Item>
                            <Input prefix={<Icon type="lock" style={{color: 'rgba(0,0,0,.25)'}}  />}  type="password"
                                   placeholder="Password" name="password" onChange={this.handleChange}/>
                        </Form.Item>
                        <Form.Item>
                            <Checkbox>Remember me</Checkbox>
                            <a className="login-form-forgot" href="">Forgot password</a>
                            <Button type="primary"  className="login-form-button" onClick={this.handleSubmit} >
                                Log in
                            </Button>
                            Or <a href="">register now!</a>
                        </Form.Item>
                    </Form>
                </Col>
            </Row>
        );
    }
}
const mapStateToProps = (state) => {
    // console.log(state);
    return {

    }
}

const mapDispatchToProps = (dispatch) => ({
        Login: (username) => dispatch({type:"LOGIN", username:username})
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(LoginRegister);
