import React, { Component } from 'react';
import {Layout, Menu, Icon, Avatar, Row, Col} from 'antd';
import Conversations from './Conversations/Conversations';
import 'antd/dist/antd.css';
import 'antd/dist/antd.min';

import qoobee from '../images/qoobee.jpg';

const leftStyle={
    overflow: 'auto', height: '100vh', position: 'fixed', left: 0,  width:'80px', backgroundColor:'#b3c4ff'
};
const rightStyle={
    overflow: 'auto', height: '100vh', position: 'fixed', left: '79px',
};

class Side extends Component{
    constructor(){
        super();
        this.state = {
            messageDisplay : 'block',
            conversationDisplay : 'none',
        }
    }

    showMessage=()=>{
        this.setState({messageDisplay:'block'});
        this.setState({conversationDisplay : 'none'});
    }

    showConversation=()=>{
        this.setState({messageDisplay:'none'});
        this.setState({conversationDisplay : 'block'});
    }

    render(){
        return(
            <div>
            <Layout style={leftStyle}
            >
                <Avatar  size={70} src={qoobee} style={{margin:'5px 5px 5px 5px'}}/>
                <Menu  inlineCollapsed={true} mode="inline" defaultSelectedKeys={['1']} style={{height: '100%',
                    backgroundColor:'#b3c4ff', marginTop:'50%'}}>
                    <Menu.Item key="1" onClick={this.showMessage}>
                        <Icon type="mail" />
                        <span>Tin nhắn</span>
                    </Menu.Item>
                    <Menu.Item key="2" onClick={this.showConversation}>
                        <Icon type="team" />
                        <span>Danh bạ</span>
                    </Menu.Item>
                </Menu>
                <Menu  inlineCollapsed={true} mode="inline" defaultSelectedKeys={['3']} >
                    <Menu.Item key="3" >
                        <Icon type="setting" />
                        <span>Cài đặt</span>
                    </Menu.Item>
                </Menu>

            </Layout>

            <Layout style={rightStyle}
            >
                    <Conversations messageHidden={this.state.messageHidden} />
            </Layout>
        </div>
        );
    }
}
export default Side;
