import {Actions} from '../Constants/Constants';
import {ChatService} from '../../Services/ChatService';

function fetch_ConversationID(conversationId) {
    return {type: Actions.CHAT_ID_FETCHING, conversationId: conversationId};
}

function get_Message(conversationId, index){
    return {type: Actions.CHAT_MESSAGE_GET, message: ChatService.getMessages(conversationId)};

    function success(message) { return { type: Actions.CHAT_MESSAGE_SUCCESS_GET, message:message} }
}

export const ChatActions = {
    fetch_ConversationID,
    get_Message,
};
