import React, { Component } from 'react';
import {Layout, Menu, Icon, Avatar, Row, Col} from 'antd';
import 'antd/dist/antd.css';
import 'antd/dist/antd.min'
import qoobee from '../images/qoobee.jpg';

const style={
    overflow: 'auto', height: '100vh', position: 'fixed', left: 0,  width:'100px', backgroundColor:'pink'
}

class Side extends Component{
    render(){
        return(

            <Layout style={style}
            >
                <Avatar  size={80} src={qoobee} style={{margin:'10px 10px 10px 10px', }}/>
                <Menu  inlineCollapsed={true} mode="inline" defaultSelectedKeys={['1']} style={{height: '100vh', width:'100px'}}>
                    <Menu.Item key="1" >
                        <Icon type="mail" />
                        <span>Tin nhắn</span>
                    </Menu.Item>
                    <Menu.Item key="2">
                        <Icon type="team" />
                        <span>Danh bạ</span>
                    </Menu.Item>
                    <Menu.Item key="3" >
                        <Icon type="setting" />
                        <span>Cài đặt</span>
                    </Menu.Item>
                </Menu>
            </Layout>
        );
    }
}
export default Side;
