import React, {Component} from 'react';
import {Alert, Avatar, Layout} from 'antd';
import qoobee from "../../images/qoobee.jpg";
import '../../css/Messages/messages.css';
import 'font-awesome/css/font-awesome.min.css';

const {
    Header, Footer, Sider, Content,
} = Layout;

const headerStyle = {
    height: '100px', backgroundColor: 'white', borderBottom: '1px solid #ebebe0 ', padding: '10px'
}

const footerStyle = {
    height: '80px', backgroundColor: 'white', borderTop: '1px solid #ebebe0 ', padding: '0px'
}

const ChatName = {
    marginTop: '-70px', marginLeft: '85px'
}

class Message extends Component {
    constructor(props) {
        super(props);
        this.state = {
            messages:[
                {   id:"1",
                    sender:'Nam',
                    type:'sent',
                    message:"bạn ăn cơm chưa"
                },
                {
                    id:"2",
                    sender:'BigBid',
                    type:'replies',
                    message:"chưa"
                },
                {
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"Bạn ăn cơm vs gì?"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"Cá"
                },
                {
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"1"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"2"
                },{
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"3"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"4"
                },{
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"5"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"6"
                },
            ]
        };
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log("typed message");
        let messageBoard = document.getElementById('messages');
        messageBoard.scroll(0, messageBoard.scrollHeight);
    }

    typeText = (event)=>{
        let code = (event.keyCode ? event.keyCode : event.which);
        if(event.keyCode === 13 && !event.shiftKey) {
            let message = event.target.value;
            let temptMessages = this.state.messages;
            temptMessages.push({
                id:"4",
                sender:'Nam',
                type: 'sent',
                message: message
            });
            this.setState({messages: temptMessages});
            // event.target.value = null;
            document.getElementById('textArea').value = "";
        }
    }


    displayMessages=()=>{
        const messages = this.state.messages.map((item)=>
            <li className={item.type}>
                <img src="http://emilcarlsson.se/assets/mikeross.png" alt=""/>
                <p>{item.message}</p>
            </li>
        );
        return messages;
    }

    onMessageScroll = (event) => {
        let messageBoard = document.getElementById('messages');
        // console.log(e.scrollTop);
        if(messageBoard.scrollTop  === 0)
        {
            alert("End");
            let messages =[
                {   id:"1",
                    sender:'Nam',
                    type:'sent',
                    message:"Nà ní"
                },
                {
                    id:"2",
                    sender:'BigBid',
                    type:'replies',
                    message:"nà ní 2"
                },
                {
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"nà ní 2"
                }];
            this.appendMessages(messages);
            messageBoard.scroll(0, 10);
        }
    }

    appendMessages=(messages)=>{
        let temptMessages = messages;
        temptMessages.concat(this.state.messages);
        this.setState({messages: temptMessages});
        console.log(this.state.messages);
    }


    render() {
        return (
            <Layout style={{borderLeft: "1px solid #ebebe0"}} >
                <Header style={headerStyle}>
                    <Avatar size={80} src={qoobee}/>
                    <h3 style={ChatName}>Ms. QooBee</h3>
                </Header>
                <div className="messages" onScroll={this.onMessageScroll} id="messages">
                    <ul >
                        {this.displayMessages()}
                    </ul>
                </div>

                <Footer style={footerStyle}>
                    <div className="message-input">
                        <div className="wrap">
                            <form>
                                <textarea id="textArea" style={{backgroundColor:'pink'}} placeholder="Write your message... "  onKeyDown={this.typeText}></textarea>
                            </form>
                            <div className="tool">
                                <button className="submit"><i className="fa fa-paper-plane" style={{fontSize:'24px'}}></i></button>
                            </div>

                        </div>
                    </div>
                </Footer>

            </Layout>
        );
    }
}

export default Message;
