import React, {Component} from 'react';
import {Alert, Avatar, Layout} from 'antd';
import qoobee from "../../images/qoobee.jpg";
import '../../css/Messages/messages.css';
import 'font-awesome/css/font-awesome.min.css';

const {
    Header, Footer, Sider, Content,
} = Layout;

const headerStyle = {
    height: '100px', backgroundColor: 'white', borderBottom: '1px solid #ebebe0 ', padding: '10px'
}

const footerStyle = {
    height: '80px', backgroundColor: 'white', borderTop: '1px solid #ebebe0 ', padding: '0px'
}

const ChatName = {
    marginTop: '-70px', marginLeft: '85px'
}

class Message extends Component {
    constructor(props) {
        super(props);
        this.state = {
            messages:[
                {   id:"1",
                    sender:'Nam',
                    type:'sent',
                    message:"bạn ăn cơm chưa"
                },
                {
                    id:"2",
                    sender:'BigBid',
                    type:'replies',
                    message:"chưa"
                },
                {
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"Bạn ăn cơm vs gì?"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"Cá"
                },
                {
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"1"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"2"
                },{
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"3"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"4"
                },{
                    id:"3",
                    sender:'Nam',
                    type:'sent',
                    message:"5"
                },
                {
                    id:"4",
                    sender:'BigBid',
                    type:'replies',
                    message:"6"
                },
            ]
        };
    }

    textOnChange = (event)=>{
        var code = (event.keyCode ? event.keyCode : event.which);
        if(code == 13) {

        }
    }

    displayMessages=()=>{
        const messages = this.state.messages.map((item)=>
            <li className={item.type}>
                <img src="http://emilcarlsson.se/assets/mikeross.png" alt=""/>
                <p>{item.message}</p>
            </li>
        );
        return messages;
    }

    onMessageScroll = (event) => {
        var e = document.getElementById('messages');
        console.log(e.scrollTop);
        if(e.scrollTop  === 0)
        {
            alert("End");
        }
        e.scroll
    }



    render() {
        return (
            <Layout style={{borderLeft: "1px solid #ebebe0"}} >
                <Header style={headerStyle}>
                    <Avatar size={80} src={qoobee}/>
                    <h3 style={ChatName}>Ms. QooBee</h3>
                </Header>
                <div className="messages" onScroll={this.onMessageScroll} id="messages">
                    <ul >
                        {/*<li className="sent">*/}
                            {/*<img src="http://emilcarlsson.se/assets/mikeross.png" alt=""/>*/}
                            {/*<p>How the hell am I supposed to get a jury to believe you when I am not even sure that*/}
                                {/*I*/}
                                {/*do?!</p>*/}
                        {/*</li>*/}
                        {/*<li className="replies">*/}
                            {/*<img src="http://emilcarlsson.se/assets/harveyspecter.png" alt=""/>*/}
                            {/*<p>When you're backed against the wall, break the god damn thing down.</p>*/}
                        {/*</li>*/}
                        {/*<li className="replies">*/}
                            {/*<img src="http://emilcarlsson.se/assets/harveyspecter.png" alt=""/>*/}
                            {/*<p>Excuses don't win championships.</p>*/}
                        {/*</li>*/}
                        {/*<li className="sent">*/}
                            {/*<img src="http://emilcarlsson.se/assets/mikeross.png" alt=""/>*/}
                            {/*<p>Oh yeah, did Michael Jordan tell you that?</p>*/}
                        {/*</li>*/}
                        {/*<li className="replies">*/}
                            {/*<img src="http://emilcarlsson.se/assets/harveyspecter.png" alt=""/>*/}
                            {/*<p>No, I told him that.</p>*/}
                        {/*</li>*/}
                        {/*<li className="replies">*/}
                            {/*<img src="http://emilcarlsson.se/assets/harveyspecter.png" alt=""/>*/}
                            {/*<p>What are your choices when someone puts a gun to your head?</p>*/}
                        {/*</li>*/}
                        {/*<li className="sent">*/}
                            {/*<img src="http://emilcarlsson.se/assets/mikeross.png" alt=""/>*/}
                            {/*<p>What are you talking about? You do what they say or they shoot you.</p>*/}
                        {/*</li>*/}
                        {/*<li className="replies">*/}
                            {/*<img src="http://emilcarlsson.se/assets/harveyspecter.png" alt=""/>*/}
                            {/*<p>Wrong. You take the gun, or you pull out a bigger one. Or, you call their bluff. Or,*/}
                                {/*you*/}
                                {/*do any one of a hundred and forty six other things.</p>*/}
                        {/*</li>*/}
                        {this.displayMessages()}
                    </ul>
                </div>

                <Footer style={footerStyle}>
                    <div className="message-input">
                        <div className="wrap">
                            <form>
                                <textarea placeholder="Write your message... "  onKeyPress={this.textOnChange}></textarea>
                            </form>
                            <div className="tool">
                                <button className="submit"><i className="fa fa-paper-plane" style={{fontSize:'24px'}}></i></button>
                            </div>

                        </div>
                    </div>
                </Footer>

            </Layout>
        );
    }
}

export default Message;
