import React, {Component} from 'react';
import Side from './Side/Side.js';
import Messages from "./Messages/Messages";
import LoginRegister from './Login-Register/Login-Register';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";
import Authentication from './Authentication';

import {
    Layout, Menu, Breadcrumb, Icon, Button,
} from 'antd';
import Message from "./Messages/Message";
import connect from "react-redux/es/connect/connect";


const {
    Header, Content, Footer, Sider,
} = Layout;

var userName = 'Nà ní';

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={(props) => (
        Authentication.isAuthenticated === true
            ? <Component {...props} username={userName} />
            : <Redirect to={{
                pathname: '/login',
                state: { from: props.location }
            }} />
    )} />
)

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username:'123',
        };
    }

    componentDidMount() {
        var connection = new WebSocket('ws://localhost:8081/echo');
        connection.onopen = function () {
            connection.send('Ping'); //
        };
        connection.onerror = function (error) {
            console.log('WebSocket Error ' + error);
        };
        connection.onmessage = function (e) {
            console.log('Server: ' + e.data);
        };

    }
    render() {

        const { username } = this.props;

        console.log("SSSS", username);

        if(localStorage.getItem("isLogin")){
            return (
                <Layout>
                    {/*<LoginRegister/>*/}
                </Layout>
            );
        }
        else{
            return(
                <Router>
                    <div>
                        <Route path="/login" component={LoginRegister}/>
                        <Route path="/home" component={Side} />
                        <PrivateRoute exact path='/' component={Side} />
                    </div>
                </Router>
            );
        }

    }
}
const mapStateToProps = (state) => ({
   // this.setState({username:'Nà ní'});
   //  console.log("fgf", state.userReducer.username);
    username: state.userReducer.username
    // username:state.userReducer.username;
    // return {
    //
    // }
})
export default connect(
    mapStateToProps
)(App);
