import { Redirect } from 'react-router-dom'


function login(username, password) {
        UserService.login(username, password)
            .then(user =>{
                console.log(user.username);
                localStorage.setItem('user', JSON.stringify(user));
                // history.push('/');
                <Redirect to='/login' />
