import React, {Component} from 'react';
import {
    Form, Icon, Input, Button, Checkbox, Row, Col,
} from 'antd';

class LoginRegister extends Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                username: '',
                password: ''
            },
            submitted: false
        };
    }
    handleChange=(event)=>{
        console.log(event.target.value);
        // const { name, value } = event.target;
        // const { user } = this.state;
        // this.setState({
        //     user: {
        //         ...user,
        //         [name]: value
        //     }
        // });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.user.username);
    }

    render() {
        return (
            <Row style={{marginTop:'200px'}}>
                <Col span={8} offset={8}>
                    <Form onSubmit={this.handleSubmit} className="login-form">
                        <Form.Item>
                            <Input  prefix={<Icon type="user" style={{color: 'rgba(0,0,0,.25)'}} name="username" onChange={this.handleChange}/>}
                                   placeholder="Username"/>
                        </Form.Item>
                        <Form.Item>
                            <Input prefix={<Icon type="lock" style={{color: 'rgba(0,0,0,.25)'}} name="password" onChange={this.handleChange}/>} type="password"
                                   placeholder="Password"/>
                        </Form.Item>
                        <Form.Item>
                            <Checkbox>Remember me</Checkbox>
                            <a className="login-form-forgot" href="">Forgot password</a>
                            <Button type="primary"  className="login-form-button" onClick={this.handleSubmit} >
                                Log in
                            </Button>
                            Or <a href="">register now!</a>
                        </Form.Item>
                    </Form>
                </Col>
            </Row>
        );
    }
}

export default LoginRegister;
